package ClearCode;

public class Write_a_program_to_swap_two_variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=2;
		int b=3;
		
		System.out.println(a+""+b);
		
	a=a+b;
	b=a-b;
	a=a-b;
	
	System.out.println("A value"+a);
	System.out.println("B value"+b);
		

	}

}
